// import ContactForm from "./components/Contact";
// import Footer from './components/Footer';

function App() {
  const [currentPage, handlePageChange] = useState("About me");

  const renderPage = () => {
    switch(currentPage) {
      case "About me":
      //   return <About />;
      // case "Portfolio":
      //   return <Portfolio />;
      // case "Contact":
      //   return <ContactForm />;
      // case "Resume":
      //   return <Resume />;
      default: 
        return <About />;
    }
  };
  return (
    <div>
      <Header currentPage={currentPage} handlePageChange={handlePageChange}/>
      {renderPage()}
      <Footer/>
    </div>   
  );
}

export default App;
